import React,{useRef} from "react"
import Snavbar from "./Snavbar"
import "./Slogin.css"
import "./Shome.css"
export default function Shome(){
  const [credentials, setCredentials] = useState({ email: "", password: "" });

  const handleSubmit = async (event) => {
    event.preventDefault();
    ce.preventDefault();
    const response = await fetch("http://localhost:5000/api/details/add", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify({name: credentials.name,email: credentials.email,contact:credentials.contact})
    })
    const json=await response.json();
    console.log(json);
    if(json.success){
        navigate('/');
        alert("successful creation");
    }
    else{
        alert("error");
    }
  }
  const handleChange=(e)=>{
    e.preventDefault();
    setCredentials({...credentials,[e.target.name]:e.target.value})
}  
        const formRef=useRef();
        return (
        <div className="addcomp">
    <Snavbar/>

    <div className="container-fluid">
      <div className="row justify-content-center">
        <div className="col-12 col-md-8 glass-container">
          <div className="mt-1 nav d-flex justify-content-center">
            <h2>ENTER DETAILS BELOW:</h2>
          </div>
          <form ref={formRef} onSubmit={handleSubmit}>
            <div className="row">
              <div className="col-md-6">
                <div className="mb-2">
                  <label htmlFor="Name" className="form-label">
                    Name:
                  </label>
                  <input
                    name="name"
                    type="text"
                    className="form-control"
                    aria-describedby="emailHelp"
                    onChange={handleChange}
                  />
                </div>
                <div className="mb-2">
                  <label htmlFor="email" className="form-label">
                    email
                  </label>
                  <input
                    name="email"
                    type="email"
                    className="form-control"
                    onChange={handleChange}
                  />
                </div>
                <div className="mb-2">
                  <label htmlFor="contact" className="form-label">
                    Contact:
                  </label>
                  <input
                    name="contact"
                    type="number"
                    className="form-control"
                    onChange={handleChange}
                  />
                </div>
                {/* <label htmlFor="resume" className="form-label">
                    Resume
                </label>
                <input type="file" className="form-control mb-5" id="inputGroupFile02" required accept='.docx, .doc, .pdf'/> */}
               </div>
               <div>
                <button type="submit" className="btn btn-dark btn-lg mt-2">Submit</button>
               </div>
            </div>
            <br />
            <br />
            {/* <div className="text-center">
              <button type="submit" className="btn btn-dark btn-lg mt-2">
                Add Company
              </button>
            </div> */}
          </form>
        </div>
      </div>
    </div>
        </div>
    )
}
